	    <h2 id="token">8.11 Token generator</h2> 
	    <section>
	    <canvas id="canvas"></canvas>      
	    <div class="col-1-3">      
    	    <div class="choixtoken">
        	    <p class="argentine">
        	    Choix de clan :<br> <SELECT name="tokenclanid" id="tokenclanid" size="1" onchange="token();" class="selector">
        	    <option title="air">air</option>
        	    <option title="eau">eau</option>
        	    <option title="esprit">esprit</option>
        	    <option title="feu">feu</option>
        	    <option title="foudre">foudre</option>
        	    <option title="glace">glace</option>
        	    <option title="illusion">illusion</option>
        	    <option title="invocation">invocation</option>
        	    <option title="mort">mort</option>
        	    <option title="nature">nature</option>
        	    <option title="poison">poison</option>
        	    <option title="telekinesie">telekinésie</option>
